import React, { useEffect, useState } from "react";
import axios from "axios";

const AvailableTimesCalendar = ({ practitionerId, appointmentType }) => {
  const [times, setTimes] = useState([]);

  useEffect(() => {
    const fetchTimes = async () => {
      try {
        const res = await axios.get(
          `http://localhost:4000/api/practitioners/${practitionerId}/available-times?appointment_type=${encodeURIComponent(
            appointmentType
          )}`
        );
        setTimes(res.data.available_times); // Adjust based on backend response
      } catch (err) {
        console.error("Error fetching available times:", err);
      }
    };

    fetchTimes();
  }, [practitionerId, appointmentType]);

  return (
    <div className="mt-4">
      <h4>Available Times for {appointmentType}</h4>
      {times.length === 0 ? (
        <p>No available times found.</p>
      ) : (
        <ul className="list-group">
          {times.map((t) => (
            <li key={t.id} className="list-group-item">
              {t.start_time} - {t.end_time}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default AvailableTimesCalendar;
